    private void jbtAccerderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtAccerderActionPerformed
        String usuario = this.jTextFieldUser.getText().trim();
        char[] arrChar = this.jPasswordFieldContra.getPassword();
        String clave = new String(arrChar);
        try {
            Llamado_BD d_emp = new Llamado_BD();
            IFLogin.objEmp = d_emp.accder(usuario, clave);

            if (IFLogin.objEmp != null) {
                Escritorio sistema = new Escritorio();
                sistema.setVisible(true);
                switch (IFLogin.objEmp.getCargo_emp()) {
                    case "Cajera":
                        sistema.dar_permiso(true, 1);
                        break;
                    case "Vendedor":
                        sistema.dar_permiso(true, 2);
                        break;
                    case "Mecánico":
                        break;
                    default:
                        sistema.dar_permiso(true, 3);
                        break;
                }
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "Empleado no encontrado.", "Acceso", JOptionPane.ERROR_MESSAGE);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jbtAccerderActionPerformed

