    private void validar_llenado(java.awt.event.KeyEvent evt, int opc) {
        //opc: 1 es para ingresar solo números
        //opc: 2 es para ingresar solo letras
        if (opc == 1) {
            char validar = evt.getKeyChar();
            if (Character.isLetter(validar)) {
                getToolkit().beep();
                evt.consume();
            }
        }
        if (opc == 2) {
            char validar = evt.getKeyChar();
            if (Character.isDigit(validar)) {
                getToolkit().beep();
                evt.consume();
            }
        }
    }

    private void validar_tamañoTexto() {
        this.jTextFieldUser.setDocument(new Tamaño_TextField(this.jTextFieldUser, 9));
        this.jPasswordFieldContra.setDocument(new Tamaño_TextField(this.jPasswordFieldContra, 8));;
    }
