    private void jbtBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtBuscarActionPerformed
        String nroOrden = this.jTextFieldOrden.getText().trim();
        if (!nroOrden.equals("")) {
            try {
                Llamado_BD datOP = new Llamado_BD();
                Pedido objOP = datOP.buscarXNroOrden(nroOrden);
                if(objOP != null) {
                    this.pedidoEncontrado = objOP;
                    Float montoTotal = datOP.obtenerTotalVenta(objOP.getCod_ped());
                    this.jTextFieldPago.setText(montoTotal.toString());
                } else {
                    JOptionPane.showMessageDialog(this, "No se encontrÃ³ Orden de Pedido.", "Buscar Orden de Pedido", JOptionPane.WARNING_MESSAGE);
                }
            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, e.getMessage(), "Error en Orden de Pedido", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_jbtBuscarActionPerformed

    private void jbtVentaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtVentaActionPerformed
        if(this.pedidoEncontrado != null){
            Venta objV = new Venta();
            
            Calendar cal = Calendar.getInstance();
            Timestamp fecha_hora = new Timestamp(cal.getTimeInMillis());
            String fecha_nuevo = String.valueOf(fecha_hora);
            String Fecha = fecha_nuevo.substring(0, 19);
            
            objV.setFechaHora(Fecha);
            objV.setMontoPagar(Float.parseFloat(this.jTextFieldPago.getText()));
            objV.setId_ped(this.pedidoEncontrado);
            
            try {
                InsertarBD datV = new InsertarBD();
                datV.insertarVenta(objV);
                JOptionPane.showMessageDialog(this, "Se registrÃ³ la venta.", "Generar Venta", JOptionPane.INFORMATION_MESSAGE);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, e.getMessage(), "Error en Orden de Pedido", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_jbtVentaActionPerformed

